import React, { Component } from 'react';


class ColerPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: [
        'red',
        'green',
        'blue'
      ]
    }
  }

  showColor (colorItem) {

    return {
      background : colorItem,
    }
  }

  setActiveColor = (color) => {
    onReceiveColor
  }

  render() {

    const { colors } = this.state;
    const { color , onReceiveColor } = this.props;

    return (
      <div className="control__color">
        <div className="title">
          Color picker
        </div>
        <div className="list">
          {colors.map((colorItem, index) => 
            { 
              return <span 
                        key={index}   
                        style = {this.showColor(colorItem)}
                        className = {color === colorItem ? 'active' : ''}
                        onClick = {() =>{this.setActiveColor(colorItem)}}
                      >
                      </span> 
            })
          }
        </div>
      </div>
    );
  }

}

export default ColerPicker;
