import React, { Component } from 'react';
import './App.css';
import TodoHeader from './components/todoheader';
import TodoBody from './components/todobody';
import TodoFooder from './components/todofooder';

var randomstring = require("randomstring"); 


class App extends Component {
  constructor(props){
    super(props);
    this.state ={
      todoItem : [
         {
          id:'',
          content : '',
          isComplete : false
        }
      ]
    }
  }

  randomID = () => {
    return randomstring.generate(7) + '-' + randomstring.generate(9) + '-' + randomstring.generate(12);
  }

  onCreatNewToDo = (inputVal) => {
    var newToDo = {
      id : this.randomID(),
      content : inputVal,
      isComplete : false
    }

    return newToDo;
  }

  onPushToDoList = () => {
    this.setState({
      todoItem : [
        ...this.state.todoItem,
        this.onCreatNewToDo()
      ]
    });
  }

  render() {
    return (
      <div className="App">
        <div className="todos">
          <TodoHeader
            inputVal = { this.onCreatNewToDo }
          />
          <TodoBody />
          <TodoFooder />
        </div>
      </div>
    );
  }

}

export default App;
