import React, { Component } from 'react';
import { connect } from 'react-redux';


class TodoHeader extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {

  //   }
  // }

  handlerInput = (event) => {
    let target = event.target;
    let value = target.value; 
    return value;  
  }

  addItem = () => {
    let inputVal= handlerInput();
    this.props.addItem(inputVal);
  }


  render() {
    // const { inputVal } = this.props;
    return (
      <div className="todos__header">
        <h1>Todo List</h1>
        <div className="form">
          <input
            type="text"
            placeholder="Nhập công việc cần làm"
            name="txtInput"
            onChange={this.handlerInput}
          />
          <button
            className="btn btn-addItem"
            onClick={this.addItem}
          >
            Add
          </button>
        </div>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return{};
}

const mapDispatchToProps = (dispatch, action) => {
  return {
    addItem : (inputVal) => {
      dispatch(action.addItem(inputVal));
    }
  }
}



export default connect(mapStateToProps, mapDispatchToProps)(TodoHeader)
