import * as types from '../constants/Actiontype';                                                            

var initialState = [];

var myReducer = ( state = initialState, action) => {
    switch(action.type){
        case types.LIST_ALL:
            state = JSON.parse(localStorage.getItem('todoItems'));
            console.log(state)
            return state;
        default : return state;
    }

}

export default myReducer;
