import React, { Component } from 'react';

class TodoItem extends Component {

    onCheck = (index) => {
        this.props.checkComplete(index);
    }

    render() {
        const { index, isComplete, content } = this.props;
        return (
            <div className={`todos__item ${isComplete === true ? `active` : ``}`}>
                <div
                    className="item-state"

                >
                    {isComplete === true ? 
                    <i className="fas fa-check-square"  onClick={this.onCheck(index)}></i> : <i className="far fa-check-square"  onClick={this.onCheck(index)}></i>}
                </div>
                <h3 className="item-content">{content}</h3>
                <div className="btn btn-remove">
                    <i className="fas fa-trash-alt"></i>
                </div>
            </div>

        );
    }

}

export default TodoItem;
