import React, { Component } from 'react';
import './App.css';
import TodoHeader from './components/todoheader';
import TodoBody from './components/todobody';
import TodoFooder from './components/todofooder';

var randomstring = require("randomstring");


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoItem: [

      ]
    }
  }

  randomID = () => {
    return randomstring.generate(7) + '-' + randomstring.generate(9) + '-' + randomstring.generate(12);
  }

  onCreatNewToDo = (inputVal) => {
    // console.log(inputVal);
    var newToDo = {
      id: this.randomID(),
      content: inputVal,
      isComplete: false
    }

    return newToDo;
  }

  onPushToDoList = (dataToDo) => {
    this.setState({
      todoItem: [
        ...this.state.todoItem,
        this.onCreatNewToDo(dataToDo)
      ]
    });
  }

  onUpdateStatus = (index) => {
    console.log(index);
    let { todoItem } = this.state;
    this.setState({
      todoItem: [
        ...todoItem.slice(0, index),
        todoItem[index].isComplete = !todoItem[index].isComplete,
        todoItem[index].isComplete = !todoItem[index].isComplete,
        todoItem[index].isComplete = !todoItem[index].isComplete,

        ...todoItem.slice(index+1)
      ]
    });
  }

  render() {
    const { todoItem } = this.state;

    return (
      <div className="App">
        <div className="todos">
          <TodoHeader
            // inputVal = { this.onCreatNewToDo }
            addItem={this.onPushToDoList}
          />
          <TodoBody
            data={todoItem}
            onUpdateStatus={this.onUpdateStatus}
          />
          <TodoFooder />
        </div>
      </div>
    );
  }

}

export default App;
