import React, { Component } from 'react';

class SizeSetting extends Component {

  onUP = () =>{
    console.log('this size');
  }

  onDow = () =>{
    console.log('this size Dow');
  }

  render() {
    const { size } = this.props;
    return (
      <div className="control__size">
        <div className="title">Font size : { size } </div>
        <div className="btn">
          <button 
            className="btn__dow"
            onClick = { this.onDow }

          >
            Tăng
          </button>
          <button 
            className="btn__up"
            onClick = { this.onUp }
          >
            Giảm
          </button>

        </div>

      </div>
    );
  }

}

export default SizeSetting;
