import React, { Component } from 'react';

class Todoooder extends Component {
  constructor(props){
    super(props);
    this.state = {
      filter: this.props.filter
    }
  }

  onHandleChange = (event) => {
    console.log('t',this.state.filter)
    let target = event.target;
    // let name = target.name;
    let value = target.value;
    let { filter } = this.state;
    this.setState({
      filter :value
    });
    this.props.onFilterChange(filter); 
    console.log(this.state.filter);
  }

  render() {
    const { filter } = this.props;
    return (
      <div className="todos__fooder">
        <div className="filter">
          <h3 className="finter-content">Chọ bộ lọc để hiển thị</h3>
          <div className="filter-picker">
            <input 
              type="radio" 
              name="filter" 
              id="1"
              value={1}
              onChange = { this.onHandleChange }
              checked = { filter === 1 }
            />
            <label htmlFor="1">Đã hoàn thành</label>
            <input 
              type="radio" 
              name="filter" 
              id="-1"
              value={-1}
              onChange = { this.onHandleChange }
              checked = { filter === -1 }

            />
            <label htmlFor="-1">Chưa hoàn thành</label>
            <input 
              type="radio" 
              name="filter" 
              id="0" 
              value={0}
              onChange = { this.onHandleChange }
              checked = { filter === 0 }
            />
            <label htmlFor="0">Tất cả</label>

          </div>
        </div>
      </div>
    );
  }

}

export default Todoooder;
