import React, { Component } from 'react';

class SizeSetting extends Component {

  onUp = (prevSize) => {
    if (prevSize <= 30 ) {
      this.props.onResizeUp(prevSize + 1);
    }
  }

  onDow = (prevSize) => {
    if (prevSize > 30 ) {
      this.props.onResizeDow(prevSize -1);
    }
  }

  render() {
    const { size } = this.props;
    return (
      <div className="control__size">
        <div className="title">Font size : {size} </div>
        <div className="btn">
          <button
            className="btn__up"
            onClick={() => { this.onUp(size) }}

          >
            Tăng
          </button>
          <button
            className="btn__dow"
            onClick={() => { this.onDow(size) }}
          >
            Giảm
          </button>

        </div>

      </div>
    );
  }

}

export default SizeSetting;
