import React, { Component } from 'react';


class TodoHeader extends Component {


  handlerInput = (event,inputVal) => {
    console.log(event.target.va);
  }

  render() {
    const { inputVal } = this.props;
    return (
      <div className="todos__header">
        <h1>Todo List</h1>
        <div className="form">
          <input 
            type="text" 
            placeholder="Nhập công việc cần làm"  
            name = "txtInput"
            onChange = { this.handlerInput(inputVal) }  
            // inputVal = { inputVal }
          />
          <button className="btn btn-addItem"> Add </button>
        </div>
      </div>
    );
  }

}

export default TodoHeader;
