import React, { Component } from 'react';


class ColerPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: [
        'red',
        'green',
        'blue'
      ]
    }
  }




  showColor (colorItem) {

    return {
      background : colorItem,
    }
  }

  render() {

    const { colors } = this.state;
    let { color } = this.props;

    return (
      <div className="control__color">
        <div className="title">
          Color picker
        </div>
        <div className="list">
          {colors.map((colorItem, index) => 
            { 
              return <span 
                        key={index} 
                        style = {this.showColor(colorItem)}
                        className = {color === colors ? 'active' : ''}
                      >

                      </span> })
          }
        </div>
      </div>
    );
  }

}

export default ColerPicker;
