import React, { Component } from 'react';
import './App.css';
import TodoHeader from './components/todoheader';
import TodoBody from './components/todobody';
import TodoFooder from './components/todofooder';

var randomstring = require("randomstring");


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todoItem: [

      ],
      filter : 0
    }
  }

  randomID = () => {
    return randomstring.generate(7) + '-' + randomstring.generate(9) + '-' + randomstring.generate(12);
  }

  onCreatNewToDo = (inputVal) => {
    // console.log(inputVal);
    var newToDo = {
      id: this.randomID(),
      content: inputVal,
      isComplete: false
    }
    return newToDo;
  }

  onPushToDoList = (dataToDo) => {
    this.setState({
      todoItem: [
        ...this.state.todoItem,
        this.onCreatNewToDo(dataToDo)
      ]
    });
  }

  onUpdateStatus = (index) => {
    console.log(index);
    let { todoItem } = this.state;
    todoItem[index].isComplete = !todoItem[index].isComplete;
    this.setState({
      todoItem: todoItem,
    });
  }

  removeItem = (index) => {
    console.log(index);
    let { todoItem } = this.state;
    this.setState({
      todoItem:[
        ...todoItem.slice(0,index),
        ...todoItem.splice(index-1,0),
        ...todoItem.slice(index+1)
      ]
    });
  }

  onFilterChange = (value) => {
    this.setState({
      filter : value
    });
  }

  onFilter = (todoItems,filter) => {
    if(filter === 1){
      return todoItems.filter((todoItem) => {
        return todoItem.isComplete === true;
      })
    }
    if(filter === -1){
      return todoItems.filter((todoItem) => {
        return todoItem.isComplete === false;
      })
    }
    else{
      return todoItems;
    }

  }

  render() {
    const { todoItem, filter } = this.state;
    return (
      <div className="App">
        <div className="todos">
          <TodoHeader
            // inputVal = { this.onCreatNewToDo }
            addItem={this.onPushToDoList}
          />
          <TodoBody
            data={todoItem}
            onUpdateStatus={this.onUpdateStatus}
            removeItem={this.removeItem}
          />
          <TodoFooder 
            filter= {filter}
            onFilterChange = { this.onFilterChange }
          />
        </div>
      </div>
    );
  }

}

export default App;
