import React, { Component } from 'react';

class SizeSetting extends Component {


  render() {
    const { size } = this.props;
    return (
      <div className="control__size">
        <div className="title">Font size : {size
        } </div>
        <div className="btn">
          <button className="btn__dow">Tăng</button>
          <button className="btn__up">Giảm</button>

        </div>

      </div>
    );
  }

}

export default SizeSetting;
