import React, { Component } from 'react';
import './App.css';
// import '../Source HTML/css/app.css';

import ColorPicker from './components/ColerPicker';
import SizeSetting from './components/SizeSetting';
import Result from './components/Result';



class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      color : 'red',
      fontSize : 15
    }
  }

  onSetColor = (params) => {
    this.setState ({
      color : params,
    });

  }

  render() {
    let { color, fontSize } = this.state;
    return (
      <div className="App">
        <div className="control">
        <ColorPicker
          color = { color } 
          onReceiveColor = {this.onSetColor}
        />
        <SizeSetting
          size = { fontSize }
          onResize
        />

        </div>

        <Result/>

      </div>
    );
  }

}

export default App;
