import React, { Component } from 'react';

class Result extends Component {

  showResilt = (size, color) => {
    return {
      color : color,
      fontSize : size, 
      boderColor : color
    }
  }

  render() {
    const { size, color } = this.props;



    return (

      <div className="result" style = { this.showResilt(size, color) }>
      {}
        <span

        >
          Kết quả sau khi điều khiển.
        </span>

      </div>

    );
  }

}

export default Result;
